# string formating



import sys 
# 'F' based formating
 
a='Jai'
b='Shree'
c='Ram'
print(f'''{a} {b} {c}''') 






# keybased formating
sys.exit(0)
a='Jai'
b='Shree'
c='Ram'
print('this is {first} the {second} string {third}'.format (first=a, second=b,third=c))

sys.exit(0)
# this is possitional formating:-
a='Jai'
b='Shree'
c='Ram'
print('{0} this is {1} the string {2}'.format(a,b,c)) 

sys.exit(0)
a='Jai'
b='Shree'
c='Ram'
print("this is the string",a,b,c,)

